import 'package:flutter/material.dart';class Util {  Future<void> showMyDialog(    BuildContext context,    String title,    String message,  ) {    return showDialog(        context: context,        builder: (BuildContext context) {          return AlertDialog(            title: Text(title),            content: Text(message),            actions: [              ElevatedButton(                  onPressed: () {                    Navigator.pop(context);                  },                  child: const Text('OK'))            ],          );        });  }}